trigger.extras:
global_artist = getString("artist");
global_track = getString("track");
global_album = getString("album");

action.script:
if (getAndroidSDKVersion() == 23)
{
androidVersion = "6.0 Marshmallow"
}

if (getAndroidSDKVersion() == 22)
{
androidVersion = "5.1 Lollipop"
}

if (getAndroidSDKVersion() == 21)
{
androidVersion = "5.0 Lollipop"
}

if (getAndroidSDKVersion() == 19)
{
androidVersion = "4.4 KitKat"
}

if (getAndroidSDKVersion() == 18)
{
androidVersion = "4.3 Jelly Bean"
}

if (getAndroidSDKVersion() == 17)
{
androidVersion = "4.2 Jelly Bean"
}

if (getAndroidSDKVersion() == 16)
{
androidVersion = "4.1 Jelly Bean"
}

global_artist = "no soundplayer activ";
global_track = "no soundplayer activ";
global_album = "no soundplayer activ";
